$(document).ready(function () {
  // $('.owl-carousel').owlCarousel({

  // });
  var topOffset = $("#nav-items").outerHeight();
  $('.nav-lists a').on('click', function (event) {
    $('.nav-lists .active').removeClass('active')
    $(this).parent().addClass("active");
    $(this).addClass('active').siblings().removeClass('active');
    var target = $(this.getAttribute("href"));
    if (target.length) {
      event.preventDefault();
      $('html, body').animate({
        scrollTop: target.offset().top - topOffset
      }, "slow");

    }


  });
  // show hide about skill 
  $('.btn-skill').on('click', function () {
    const value = $(this).attr('data-filter');
    if (value === '') {
      $('.show-skill:first').show(500);

    } else {
      $('.show-skill').not('.' + value).hide(500);
      $('.show-skill').filter('.' + value).show(500);

    }


  });
  
  // Go to top 
  $(window).on("scroll", function () {
    if ($(this).scrollTop() > 200) {
      $(".to-top").show();
    } else {
      $(".to-top").hide();

    }
  });
  $(".to-top").on("click", function () {
    $('html, body').animate({
      scrollTop: 0
    }, 'slow');
    return false;
  });

  //  Filter Project 
  $('.list-project').on('click', function () {
    const value = $(this).attr('data-filter');
    if (value === 'all') {
      $('.itemProject').show(500);
    } else {
      $('.itemProject').not('.' + value).hide(500);
      $('.itemProject').filter('.' + value).show(500);

    }
  });
  // Add active Filter Project 
  $('.list-project').on('click', function () {
    $(this).addClass('active').siblings().removeClass('active');
  });

// hide show alert 
  $("#bnt-hide-alert").click(function(){
  $("#alert-body").hide();
  });



});

// Onscroll 
const nav = document.querySelector('.nav-bg');
window.addEventListener("scroll", function () {
  nav.classList.toggle("sticky", window.scrollY > 0);
});


// active btn skill 
// Add active class to the current button (highlight it)
var selector = '.btn-skills .btn-skill';

$(selector).on('click', function () {
  $(selector).removeClass('skill-active');
  $(this).addClass('skill-active');
});



menuBtn = document.getElementById('btn-menu');
navBar = document.getElementById('nav-lists');
const navLi = document.querySelectorAll('.nav-li');

menuBtn.addEventListener('click', () => {
  navBar.classList.toggle('show');
  menuBtn.classList.toggle('open');
  document.body.classList.toggle('no-scroll');
})

navLi.forEach((li) => {
  li.addEventListener('click', () => {
    navBar.classList.remove('show');
    menuBtn.classList.remove('open');
    document.body.classList.remove('no-scroll');


  });
});






